project/contracts/HotelBooking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title HotelBooking
 * @dev Contract xử lý logic đặt phòng khách sạn bằng HotelToken
 * Users phải approve token trước khi có thể đặt phòng
 */
contract HotelBooking is Ownable {
    IERC20 public paymentToken; // Địa chỉ của HotelToken contract

    /**
     * @dev Struct lưu thông tin phòng
     */
    struct Room {
        uint256 id;
        uint256 price; // Giá tính bằng Token (đã có 18 decimals)
        bool isBooked;
        address bookedBy; // Địa chỉ người đặt phòng
    }

    // Mapping từ roomId => Room
    mapping(uint256 => Room) public rooms;
    
    /**
     * @dev Event được emit khi có người đặt phòng thành công
     */
    event RoomBooked(uint256 indexed roomId, address indexed user, uint256 price);

    /**
     * @dev Constructor
     * @param _tokenAddress Địa chỉ của HotelToken contract
     */
    constructor(address _tokenAddress) Ownable(msg.sender) {
        require(_tokenAddress != address(0), "Token address cannot be zero");
        paymentToken = IERC20(_tokenAddress);
    }

    /**
     * @dev Admin set thông tin phòng (giá, id)
     * @param _id ID của phòng
     * @param _price Giá phòng tính bằng Token (sẽ tự động nhân với 10^18)
     */
    function setRoom(uint256 _id, uint256 _price) external onlyOwner {
        require(_price > 0, "Price must be greater than 0");
        rooms[_id] = Room({
            id: _id,
            price: _price * 10**18, // Convert sang wei (18 decimals)
            isBooked: false,
            bookedBy: address(0)
        });
    }

    /**
     * @dev User gọi hàm này để đặt phòng
     * Yêu cầu: User phải approve token cho contract này trước
     * @param _id ID của phòng muốn đặt
     */
    function bookRoom(uint256 _id) external {
        Room storage room = rooms[_id];
        
        // Kiểm tra phòng đã có người đặt chưa
        require(!room.isBooked, "Phong da co nguoi dat");
        
        // Kiểm tra phòng có tồn tại không (price > 0)
        require(room.price > 0, "Phong khong ton tai");

        // Transfer token từ user về contract
        // User phải approve trước đó rồi
        bool success = paymentToken.transferFrom(msg.sender, address(this), room.price);
        require(success, "Thanh toan that bai (Vui long Approve truoc)");

        // Cập nhật trạng thái phòng
        room.isBooked = true;
        room.bookedBy = msg.sender;

        // Emit event
        emit RoomBooked(_id, msg.sender, room.price);
    }

    /**
     * @dev Admin rút toàn bộ token từ contract về ví của mình
     */
    function withdrawTokens() external onlyOwner {
        uint256 balance = paymentToken.balanceOf(address(this));
        require(balance > 0, "No tokens to withdraw");
        
        bool success = paymentToken.transfer(owner(), balance);
        require(success, "Withdraw failed");
    }

    /**
     * @dev Lấy thông tin phòng
     * @param _id ID của phòng
     * @return Room struct với thông tin phòng
     */
    function getRoom(uint256 _id) external view returns (Room memory) {
        return rooms[_id];
    }

    /**
     * @dev Kiểm tra phòng có sẵn không
     * @param _id ID của phòng
     * @return true nếu phòng sẵn sàng, false nếu đã được đặt hoặc không tồn tại
     */
    function isRoomAvailable(uint256 _id) external view returns (bool) {
        Room memory room = rooms[_id];
        return !room.isBooked && room.price > 0;
    }
}
